import { useState } from "react";


export default function Board() {
  const [value, setValue] = useState(null);
  function handleClick(){
    console.log("clicked");
    setValue('X');
  };
  return (
    <>
      <div className="board-row">
        <Square value={value} aa={handleClick}/>
        <Square value={value} aa={handleClick}/>
        <Square value={value} aa={handleClick}/>
      </div>
      <div className="board-row">
        <Square value={value} aa={handleClick}/>
        <Square value={value} aa={handleClick}/>
        <Square value={value} aa={handleClick}/>
      </div>
      <div className="board-row">
        <Square value={value} aa={handleClick}/>
        <Square value={value} aa={handleClick}/>
        <Square value={value} aa={handleClick}/>
      </div>
    </>
  );
}
function Square({value, aa}){
  return (
  <button className="square" onClick={aa}>{value} </button>
  );
}
